mapper.select:
SELECT Article.*,User.img AS writerImg, User.name AS writerName FROM Article JOIN User On Article.writerId=User.id WHERE Article.deletedAt IS NULL AND ( CASE WHEN #{key}='name' THEN Article.isAnonymous=0 AND Article.writerId = any(SELECT id FROM User WHERE name LIKE concat ('%', #{word}, '%')) ELSE ${key} LIKE concat('%', #{word}, '%') END)
ORDER BY Article.regDate DESC
ORDER BY ${orderBy} ${orderByDir}
