mapper.delete:
DELETE FROM `User`
WHERE id
= #{id} AND password = #{password}

mapper.select:
SELECT *
FROM `User`
WHERE numId =#{numId} AND password = #{password}

mapper.update:
UPDATE `User`
SET phoneNumber = #{phoneNumber}, email = #{email},
isEmployed = #{isEmployed}

	, password = #{password}


	, img = #{img}

WHERE numId = #{numId}
